parameters.body:
={
  "type": "function",
  "function": {
    "name": "extractReservationData",
    "async": false,
    "description": "Extrae los datos clave de una reserva a partir de la transcripción de la llamada.",
    "parameters": {
      "type": "object",
      "properties": {
        "hora": {
          "description": "Hora de la reserva en formato de 24H, por ejemplo: 19:00",
          "type": "string"
        },
        "nombre": {
          "description": "Nombre de la persona que solicita la reserva",
          "type": "string"
        },
        "reserva_fecha": {
          "description": "Fecha de la reserva en formato \"yyyy-mm-dd\", por ejemplo: 2025-12-12",
          "type": "string"
        },
        "reserva_invitados": {
          "description": "Número de invitados en la reserva por ejemplo: 3",
          "type": "number"
        }
      },
      "required": [
        "hora",
        "nombre",
        "reserva_fecha",
        "reserva_invitados"
      ]
    }
  }
}
